#SET NAMES UTF8;
#DROP DATABASE IF EXISTS hmz;
#CREATE DATABASE hmz CHARSET=UTF8;
#USE hmz;


#笔记本电脑型号家族
CREATE TABLE hmz_flower_family(
  fid INT PRIMARY KEY AUTO_INCREMENT,
  fname VARCHAR(32)
);

#笔记本电脑
CREATE TABLE hmz_flower(
  lid INT PRIMARY KEY AUTO_INCREMENT,
  title VARCHAR(32),            #标题
  mp4 VARCHAR(128),             #视频
  classify VARCHAR(32),         #所属分类
  dtime VARCHAR(32),                   #上架时间
  explains VARCHAR(128),        #解释标题
  detailed VARCHAR(256),        #详细解释标题
  theme VARCHAR(64),            #主题
  introduce VARCHAR(8000),      #产品详细说明
  iscollect BOOLEAN,            #收藏
  watch INT,                    #观看量
  likes INT,                    #喜欢
  pic VARCHAR(128),             #收藏时的图片
  isVideo INT                   #是否是视频 0-false 1-true
);
#创建文章评论表
CREATE TABLE hmz_comment(
  id INT PRIMARY KEY AUTO_INCREMENT,
  flower_id INT,                #详情表id
  user_id INT,                #用户id
  content VARCHAR(128),         #评论
  ctime DATETIME,               #评论时间
  count INT                     #评论的总数量
);

#创建收藏表
CREATE TABLE hmz_collect(
  id INT PRIMARY KEY AUTO_INCREMENT,
  flower_id INT,              #详情表id
  user_id INT,                #用户id
  title VARCHAR(64),
  pic VARCHAR(128),
  watch VARCHAR(64),
  isVideo INT,              #0-false 1-true
  classify VARCHAR(32)        #所属分类
);
#创建喜欢表
CREATE TABLE hmz_like(
  id INT PRIMARY KEY AUTO_INCREMENT,
  flower_id INT,              #详情表id
  user_id INT,                #用户id
  count INT
);
#创建点赞表
CREATE TABLE hmz_zan(
  id INT PRIMARY KEY AUTO_INCREMENT,
  comment_id INT,
  flower_id INT,              #详情表id
  user_id INT,                #用户id
  count INT
);
#用户信息
CREATE TABLE hmz_user(
  uid INT PRIMARY KEY AUTO_INCREMENT,
  phone VARCHAR(16),
  upwd VARCHAR(32),
  avatar VARCHAR(128),        #头像图片路径
  user_name VARCHAR(32),      #用户名，如王小明
  gender INT                  #性别  0-女  1-男
);


#**首页轮播广告商品**
CREATE TABLE hmz_index_carousel(
  cid INT PRIMARY KEY AUTO_INCREMENT,
  img VARCHAR(128),
  title VARCHAR(64),
  href VARCHAR(128)
);

#首页九宫格
CREATE TABLE hmz_grid(
  id INT PRIMARY KEY AUTO_INCREMENT,
  pic VARCHAR(128)
);
#首页商品
CREATE TABLE hmz_index(
  pid INT PRIMARY KEY AUTO_INCREMENT,
  flower_id INT,     #详情页id
  title VARCHAR(64),
  pic VARCHAR(128),
  count VARCHAR(64),
  href VARCHAR(128),
  isVid BOOLEAN,
  every VARCHAR(32),
  video VARCHAR(32)
);

#花艺学堂
CREATE TABLE hmz_productList(
  hid INT PRIMARY KEY AUTO_INCREMENT,
  grid_id INT, 
  flower_id INT,     #详情页id
  title VARCHAR(64),
  pic VARCHAR(128),
  hname VARCHAR(32),      #小标题名
  zname VARCHAR(32),      #最新
  count VARCHAR(64),
  href VARCHAR(128),
  isVid BOOLEAN,
  biaoti VARCHAR(32)    #标题
);

#****数据导入****

#笔记本电脑型号家族
INSERT INTO hmz_flower VALUES
(1,'毕业花环|花艺课','img/video/vdo-1.mp4','花艺学堂','2019-03-25','《小姐姐的花店》花艺小课堂，教你手作一款“毕业花环”。','','本期主题「毕业花环」','<p class="cont-p cont-mar">选用花材：</p><p>月桂叶、海桐叶、满天星、洋桔梗、白色多头玫瑰</p><p class="cont-p cont-margin">节目介绍：</p><p>&nbsp;&nbsp;&nbsp;《小姐姐的花店》是由爱奇艺纯网自制的沉浸式新生活体验推广节目。</p><p>&nbsp;&nbsp;&nbsp;该节目共12期，六位花店主理人小S（徐熙娣）、宋佳、林彦俊、小鬼（王琳凯）、春夏、欧阳娜娜，在17天的时间内共同经营一家位于意大利佛罗伦萨的浪漫花店，通过多维度展现花店经营，向观众传递出“花时间，去生活”的现代生活理念。</p>',true,1820,555,'img/productList/list-1.jpg',1),
(2,'自然风盆花|花艺课','img/video/vdo-2.mp4','花艺学堂','2019-04-02','《小姐姐的花店》花艺小课堂，教你插制一款“自然风盆花”。','','本期主题「自然风盆花」','<p class="cont-p cont-mar">选用花材：</p><p>郁金香、洋桔梗、多头玫瑰</p><p class="cont-p cont-margin">节目介绍：</p><p>&nbsp;&nbsp;&nbsp;《小姐姐的花店》是由爱奇艺纯网自制的沉浸式新生活体验推广节目。</p><p>&nbsp;&nbsp;&nbsp;该节目共12期，六位花店主理人小S（徐熙娣）、宋佳、林彦俊、小鬼（王琳凯）、春夏、欧阳娜娜，在17天的时间内共同经营一家位于意大利佛罗伦萨的浪漫花店，通过多维度展现花店经营，向观众传递出“花时间，去生活”的现代生活理念。</p>',true,2000,666,'img/productList/list-2.jpg',1),
(3,'水蓝色的夏天','img/details/det-3/det-1.jpg','花艺学堂','2017-06-27','夏天的是水蓝色的！','夏日在家沾花弄草，使用蓝色、白色、绿色系的花材进行简单的搭配，就能带来清凉的视觉感受，今天我们用蓝色的绣球、白色的雪山玫瑰、尤加利和刺芹，打造一个水蓝色的夏日相框！','- 步骤 -','<p class="middle-cont-num">1</p><p>选用一个用来种植多肉植物的相框造型花器。将花泥切割成合适大小放入花器中。</p><img src="img/details/det-3/det-2.jpg" alt=""><p class="middle-cont-num">2</p><p>把小型圆叶尤加利剪成小枝铺个底。</p><img src="img/details/det-3/det-3.jpg" alt=""><p class="middle-cont-num">3</p><p>剪三朵浅蓝色绣球花，在整个正方形的相框中呈不对称三角形构图。</p><img src="img/details/det-3/det-4.jpg" alt=""><p class="middle-cont-num">4</p><p>取几枝白色雪山玫瑰，两朵或三朵组群，也呈三角形构图插入绣球之间的空间。</p><img src="img/details/det-3/det-5.jpg" alt=""><p class="middle-cont-num">5</p><p>最后用刺芹做点缀，同时用尤加利做进一步的填充。</p><img src="img/details/det-3/det-6.jpg" alt=""><p class="middle-cont-num">6</p><p>稍加调整，完成。</p><img src="img/details/det-3/det-7.jpg" alt=""><p class="middle-cont-num">7</p><p>这个相框可以水平放置于桌面，也可以立起来靠在墙边，也可以像真正的相框一样挂在墙上欣赏。</p><img src="img/details/det-3/det-8.jpg" alt="">',false,1220,615,'img/productList/list-3.jpg',0),
(4,'东方风格花束|花艺课','img/video/vdo-3.mp4','花艺学堂','2019-03-17','《小姐姐的花店》花艺小课堂，教你手作一款“东方风格花束”。','','本期主题「东方风格花束」','<p class="cont-p cont-mar">选用花材：</p><p>棕榈叶、兰花、袋鼠爪、红豆、绿石竹、百合、冬青、龙柳</p><p class="cont-p cont-mar">适合场景：</p><p>适合书房、客厅等空间场景。</p><p class="cont-p cont-margin">节目介绍：</p><p>&nbsp;&nbsp;&nbsp;《小姐姐的花店》是由爱奇艺纯网自制的沉浸式新生活体验推广节目。</p><p>&nbsp;&nbsp;&nbsp;该节目共12期，六位花店主理人小S（徐熙娣）、宋佳、林彦俊、小鬼（王琳凯）、春夏、欧阳娜娜，在17天的时间内共同经营一家位于意大利佛罗伦萨的浪漫花店，通过多维度展现花店经营，向观众传递出“花时间，去生活”的现代生活理念。</p>',true,1220,615,'img/productList/list-4.jpg',1),
(5,'网红植物x北欧风格家具','img/details/det-5/det-1.jpg','园艺花园','2018-09-07','超详细搭配设计全攻略（附案例）','','','	<p>【北欧风格特点】： 注重功能、追求理性，讲究简洁明朗的颜色，淡雅清爽的自然材质，线条流畅，不事雕琢。</p><img src="img/details/det-5/det-2.jpg" alt=""><p class="middle-cont-num">黄铜花器和波士顿蕨</p><p class="hr"></p><p>【北欧家居色彩】：会使用大面积纯色。如白色、灰色、墨绿色、米黄色（如图）等，其中黑白色也可以作为主色调或重要的点缀色，无印花、图腾的整片色彩带来另一种低调的宁静感，沉稳而内敛。</p><img src="img/details/det-5/det-3.jpg" alt=""><p class="middle-cont-num">绿萝、绿巨人、铁线蕨</p><img src="img/details/det-5/det-4.jpg" alt=""><p class="middle-cont-num">佛珠和荷兰铁</p><img src="img/details/det-5/det-5.jpg" alt=""><p class="middle-cont-num">春羽、仙人柱和鸟巢蕨</p><img src="img/details/det-5/det-6.jpg" alt=""><p class="middle-cont-num">铁线蕨、春羽和黄金绿萝</p><p class="hr"></p><p>北欧风格设计】：线条简单而流畅，十分注重细节；铁艺花架与植物容器造型简单纯粹，设计感十足。</p><img src="img/details/det-5/det-7.jpg" alt=""><img src="img/details/det-5/det-8.jpg" alt="">',false,4848,236,'img/productList/list-5.jpg',0),
(6,'家里的花这么放，好看100倍!','img/details/det-6/det-1.jpg','园艺花园','2018-12-27','家里养几盆植物，既能净化空气又能美化环境，不过，如何将植物更好的装饰我们的家居生活呢？今天小野就给大家介绍四个简单的小方法！','','','<p class="middle-cont-num">1</p><p class="middle-cont-num">组合盆栽</p><p>如果你养了不少植物，但能放植物的空间不够，不妨尝试一下组合盆栽的方式。将不同植物，按照高低错落搭配的方式组合到一起，栽培到一个大些的花盆里，如同微型的小花园，观赏价值更高哦。</p><img src="img/details/det-6/det-2.jpg" alt=""><img src="img/details/det-6/det-3.jpg" alt=""><img src="img/details/det-6/det-4.jpg" alt=""><p>在做组合盆栽的时候，要注意将生活习性相近的植物组合到一起，比如耐旱的多肉可以和仙人掌类的搭配在一起，喜阴的室内植物如竹芋类、蕨类等可以搭配在一起，这样便于统一养护管理。</p><img src="img/details/det-6/det-5.jpg" alt=""><img src="img/details/det-6/det-6.jpg" alt=""><p class="middle-cont-num">2</p><p class="middle-cont-num">花架摆放</p><p>家里有个花架来养花，是再好不过的了。花架可以摆放在任何位置，大小根据自己的需求来，花架上不但可以摆放植物，还可以添加一些园艺饰品，或摆放一些生活用品，养花生活两不误。</p><img src="img/details/det-6/det-7.jpg" alt=""><img src="img/details/det-6/det-8.jpg" alt=""><p class="middle-cont-num">3</p><p class="middle-cont-num">悬挂垂吊</p><p>将植物用绳子挂起来，可以更少的占用空间，在视觉上更有冲击力。缺点是养护起来比较麻烦，浇水时需要大家将花盆取下再养护。</p><img src="img/details/det-6/det-9.jpg" alt=""><p>悬挂的植物可以选择那些容易垂吊的植物，比如绿萝、吊兰、常春藤等，更能很好的欣赏它们垂下的枝蔓。</p><img src="img/details/det-6/det-10.jpg" alt=""><p class="middle-cont-num">4</p><p class="middle-cont-num">玻璃水培</p><p>将植物水培起来，可以赏根可以观叶，而且干净又卫生。水培植物和土培植物比起来，养护更加简单，每隔一段时间换水一次即可。水培植物可以放在室内任何地方，厨房、客厅、卧室等，透明的玻璃瓶，加上绿油油的植物，简单精致又美好。</p><img src="img/details/det-6/det-11.jpg" alt=""><img src="img/details/det-6/det-12.jpg" alt=""><p>好啦，今天小野就给大家介绍到这里了，如果你有什么养花问题，都可以给我们留言咨询哦。</p>',false,5557,874,'img/productList/list-6.jpg',0),
(7,'打造一片浪漫的玫瑰庄园','img/details/det-7/det-1.jpg','园艺花园','2017-08-10','打造一片浪漫的玫瑰庭院，让家充满幸福的气息。','','- 庭院玫瑰的品种与选择 -','<p>玫瑰的品种十分繁多，在中国我们通常将它分为玫瑰、月季、蔷薇。对于一般从事植物分类的专业人士来说不是一件难事。但是，对于一般业余爱好者来说确是一个比较困难的问题。在文学中“玫瑰”这个词也常常代指了所有的玫瑰。</p><img src="img/details/det-7/det-2.jpg" alt=""><p>玫瑰的名字既浪漫又富有诗意。一般它都带有浓郁的香气，绝大多数品种一年只开一次花，并且带有密密麻麻的枝刺，一芽多花，花枝被剪下后花朵会很快萎蔫，最长时间不会超过半小时。因此，玫瑰不适合于作鲜切花。</p><img src="img/details/det-7/det-3.jpg" alt=""><p>月季花，就没有玫瑰香味那么浓郁。一般花朵很大而且非常鲜艳，枝刺较少，花朵多单生。鲜花剪下后保鲜期长，一般可保存数天或数周。很适合作为鲜切花、盆植、园艺及绿化。人们日常生活中作为鲜切花馈赠用的“玫瑰”鲜花和盆栽玫瑰，一般都属于月季。月季的品种很多，有藤本、蔓生、灌木之分。</p><img src="img/details/det-7/det-4.jpg" alt=""><p>蔷薇，花朵通常很小，单瓣或重瓣花，每年只开一次花。多为藤本，枝繁叶茂，作为绿篱既有很好的绿化、美化效果，又可起到隔离防护作用深受人们的喜爱。</p><img src="img/details/det-7/det-5.jpg" alt=""><p>无论是高贵的玫瑰，还是随处可见的月季和蔷薇，在庭院里它们都是充满爱的美丽精灵。既然品种多种多样，不妨看到喜欢的玫瑰花色与花形后再做挑选，打造属于你自己的浪漫庭院。</p><h2>- 玫瑰庭院的造型 -</h2><p>选择一面南向的墙面，在墙面上固定网格架或勾勒出网格的铁丝，柔美曼妙的枝条可以引导至墙壁上，方便植物向上攀爬。藤本的月季就是很好的选择。</p><img src="img/details/det-7/det-6.jpg" alt=""><img src="img/details/det-7/det-7.jpg" alt=""><p>除了花墙，还可以用灌木月季、玫瑰种成花篱的模样，不仅有绿化的作用，植物身上的刺还可以防止小偷的进入。</p><img src="img/details/det-7/det-8.jpg" alt=""><img src="img/details/det-7/det-9.jpg" alt=""><p>在拱形花架或者门旁两侧栽种藤蔓玫瑰的植株。不要刻意折弯枝条，依照花架的宽度，让枝条平均分布在架子上，由下而上引导枝条沿着花架生长，一个曼妙的花门就实现了。</p><img src="img/details/det-7/det-10.jpg" alt=""><img src="img/details/det-7/det-11.jpg" alt=""><p>在玫瑰花缤纷绽放的时候，邀请朋友到家中作客，开一场玫瑰party! 献上一年播种的大礼,一边欣赏庭院的美景，一边享受劳作的快乐。</p><img src="img/details/det-7/det-12.jpg" alt="">',false,4541,999,'img/productList/list-7.jpg',0),
(8,'5月看那蔷薇开满墙','img/details/det-8/det-1.jpg','园艺花园','2018-05-17','蔷薇初夏盛开，花繁叶茂，芳香清幽，花形千姿百态，花色五彩缤纷。五月正值蔷薇开满庭院的季节，一棵棵蔷薇花蔓延生长，爬满房屋，微风过处，送来缕缕清香。','','','<p>蔷薇（学名：Rosa multiflora），又称野蔷薇，原变种是一种蔓藤爬篱笆的小花，但本种变异性强，常见庭园栽培的均为其变种。而我们通常所说的蔷薇，是这类花的通称。</p><img src="img/details/det-8/det-2.jpg" alt=""><p>蔷薇属于攀援灌木，通常无毛，有短、粗稍弯曲皮刺。奇数羽状复叶，小叶5-9，近花序的小叶有时3，连叶柄长5-10厘米；小叶倒卵形、长圆形或卵形，先端急尖或圆钝，基部近圆形或楔形，边缘有尖锐单锯齿，稀混有重锯齿，上面无毛，下面有柔毛；托叶篦齿状，大部贴生于叶柄。圆锥状花序，花梗长1.5-2.5厘米，无毛或有腺毛，花直径1.5-2厘米，花色丰富。</p><img src="img/details/det-8/det-3.jpg" alt=""><p>蔷薇花一簇一簇地盛开着，在层层叠叠的花瓣间有嗡嗡的蜜蜂钻进钻出，蝴蝶悠闲地飞来飞去，阳光而又温暖。夏初刚到，一株株盛开的蔷薇，早铺满院墙内的一侧，在绿叶的点缀下，把院内装扮的姹紫嫣红，盎然生机。</p><img src="img/details/det-8/det-4.jpg" alt=""><p>蔷薇属于攀援型灌木，因此非常适合用于立体绿化，如墙面垂直绿化，增强建筑物的生命力和美感；或者在公园、庭院的游廊、花架、拱门等构架上装饰蔷薇，形成一道亮丽的风景线。当然，也少不了插花，大花品种可以作为焦点花在作品中出现，小花品种则可以作为修饰，或者都可以单独成型哦。</p><img src="img/details/det-8/det-5.jpg" alt=""><img src="img/details/det-8/det-6.jpg" alt=""><img src="img/details/det-8/det-7.jpg" alt=""><img src="img/details/det-8/det-8.jpg" alt=""><p class="middle-cont-num">蔷薇生长习性</p><p>光照：喜阳光，亦耐半阴，较耐寒，在中国北方大部分地区都能露地越冬。</p><img src="img/details/det-8/det-9.jpg" alt=""><p>土壤：对土壤要求不严，耐干旱，耐瘠薄，但栽植在土层深厚、疏松、肥沃湿润而又排水通畅的土壤中则生长更好，也可在粘重土壤上正常生长。萌蘖性强，耐修剪，抗污染。</p><img src="img/details/det-8/det-10.jpg" alt=""><p>水分：不耐水湿，忌积水。</p><img src="img/details/det-8/det-11.jpg" alt=""><p>施肥：新株定植时应施入腐熟有机肥作为基肥。3月可施1次至2次以氮为主的液肥，促长枝叶；4月、5月施2次至3次以磷钾为主的肥料，有助于开花繁盛；花后再施一次复壮肥有助于来年生长茂盛。</p><img src="img/details/det-8/det-12.jpg" alt=""><p>修剪：蔷薇萌芽力强，生长繁盛，需及时修剪。</p><img src="img/details/det-8/det-13.jpg" alt=""><p>病虫害：蔷薇易患白粉病，一发现应将病枝剪掉销毁，并喷洒低浓度的石榴合剂或波尔多液、托布津溶液，预防病情扩散。</p><img src="img/details/det-8/det-14.jpg" alt=""><p>繁殖方法：1、常用扦插繁殖，春季或秋季，带节切取6-10厘米当年生半木质化发育健壮的枝条，插入透气性良好的土层或细沙，一般2个月左右生根。此后只需正常养护即可；2、播种繁殖，果熟采收后即行播种，或春播，播后1～2个月发芽；3、其它繁殖方式，一些名贵品种较难扦插成活，可用压条或嫁接法繁殖，无性繁殖的幼苗，当年即可开花。</p><img src="img/details/det-8/det-15.jpg" alt="">',false,3648,451,'img/productList/list-8.jpg',0),
(9,'植物如何摆放才能时髦又好看','img/details/det-9/det-1.jpg','家居搭配','2019-02-13','不得不说，绿色植物不仅能净化空气，也已成为大众备受欢迎的居家装饰。几乎每个家庭都会用或多或少的绿植点缀，那你知道如何摆放才使它们既好看又时髦吗？','','','<p class="middle-cont-num">1</p><p class="middle-cont-num">合理利用空间</p><p>植物不一定只摆放在地上或桌子上，将它们悬挂于空中，让绿色自上倾泻而下，生命力十足。</p><img src="img/details/det-9/det-2.jpg" alt=""><p>飘窗、书架、玄关、厨房等地都可以直接打几处挂钩悬挂植物。</p><img src="img/details/det-9/det-3.jpg" alt=""><p>悬挂的方式也多种多样，毛线网兜、挂钩、异型花器都能搭配的很出彩。</p><img src="img/details/det-9/det-4.jpg" alt=""><img src="img/details/det-9/det-5.jpg" alt=""><p class="middle-cont-num">2</p><p class="middle-cont-num">营造高低层次</p><p>直接摆放在地上的植物可以利用各种道具营造出高低层次。比如桌子、书架或植物支架。</p><img src="img/details/det-9/det-6.jpg" alt=""><p>在一个完整空间里，靠近沙发位置，可以借助茶几、植物支架将植物环绕沙发，更有错落感。</p><img src="img/details/det-9/det-7.jpg" alt=""><img src="img/details/det-9/det-8.jpg" alt=""><p>角落位置，可以高低错落由高到低摆放各种size的绿植，例如琴叶榕、龟背竹这类大型绿植都是靠窗角或墙角位置。</p><img src="img/details/det-9/det-9.jpg" alt=""><img src="img/details/det-9/det-10.jpg" alt=""><p>其实总结下来，摆放的方式并不难，只要根据自己的喜好，在实践中训练自己的审美和积累经验，就能总结出自己的一套植物搭配技巧。</p><img src="img/details/det-9/det-11.jpg" alt="">',false,8548,3514,'img/productList/list-9.jpg',0),
(10,'五种春节必备盆栽花，增福又添香','img/details/det-10/det-1.jpg','家居搭配','2019-01-27','新春佳节，鲜花是不可缺少的角色，既象征美好，同时寓意着吉祥。','新春佳节，在家中点缀些许花卉盆栽，可以说是锦上添花、喜上加喜了，简而言之就是“增福又添香”，那么在过年期间我们应该挑选什么花卉呢？什么才是最佳年宵花之选呢？一起来看看吧。','','<p class="middle-cont-num">1</p><p class="middle-cont-num">蝴蝶兰</p><p class="middle-cont-num">关键词：花型俊俏 花姿优雅 </p><p>蝴蝶兰是年宵花市场最惹人注意的主角之一，花色丰富，花期长。</p><img src="img/details/det-10/det-2.jpg" alt=""><p>蝴蝶兰按希腊文的意思是“好似蝴蝶般的兰花”，姿态高贵，清丽不俗，在高级宴会及婚礼现场常被用作装饰花卉，又因其观赏期长，在历年春节期间都有很高的人气。</p><img src="img/details/det-10/det-3.jpg" alt=""><p>在今天，蝴蝶兰也成为越来越多人春节送花的首选，送上一份蝴蝶兰组合盆栽作为贺礼，不落俗套，更能增添喜庆的气氛</p><img src="img/details/det-10/det-4.jpg" alt=""><p class="middle-cont-num">2</p><p class="middle-cont-num">大花蕙兰</p><p class="middle-cont-num">关键词：丰盛绚丽 气质不俗</p><p>大花惠兰，叶长碧绿，是世界著名的"兰花新星"。其花朵硕大，色彩绚丽，其花型整齐且质地坚挺，经久不凋，恰逢春节期间正是大花蕙兰的开花期，因此也是春节必不可少的人气花卉之一。</p><img src="img/details/det-10/det-5.jpg" alt=""><p>作为尤为受欢迎的年宵花的一种、春节花市中绝对的抢手货，大花蕙兰花色泽显现，花型美、花朵大，摆在家中绝对抢眼，常有嫩绿、深红等鲜见色彩，清雅别致，气质脱俗，这样美丽的花来映衬节日的氛围再适合不过了，绝对吸睛又有新年气氛。</p><img src="img/details/det-10/det-6.jpg" alt=""><p class="middle-cont-num">3</p><p class="middle-cont-num">红掌</p><p class="middle-cont-num">关键词：色彩红艳 吉祥喜庆</p><p>红掌是全年都可观赏的花，形状、颜色和质感都非常有特色。她翠叶欲滴，佛焰苞片猩红亮丽，红红火火的花朵在春节期间开放可以说实在是太应景了，完美的呼应了新年喜庆的气氛，作为春节期间的装饰花朵再好不过。 </p><img src="img/details/det-10/det-7.jpg" alt=""><img src="img/details/det-10/det-8.jpg" alt=""><p class="middle-cont-num">4</p><p class="middle-cont-num">仙客来</p><p class="middle-cont-num">关键词：新春应景 仙客翩翩而至</p><p>仙客来花瓣反卷，像小兔子的耳朵一样灵巧可爱，花型独特、舒展烂漫，素来有“仙客翩翩而至”之意，非常应春节气氛，开花之后香气若有若无，花形别致，是冬春季节的人气盆花，不但能添喜招财，还能吸收室内有害气体、净化空气，是过年花市的畅销花之一。</p><img src="img/details/det-10/det-9.jpg" alt=""><p>仙客来花期适逢圣诞节、元旦、春节等传统节日，可以达到5个月之久。但开的最好的时候恰巧是过年期间，实在是太适合新年摆放了，粉色最受欢迎，尤其能给家里增添气氛。</p><img src="img/details/det-10/det-10.jpg" alt=""><p class="middle-cont-num">5</p><p class="middle-cont-num">山茶花</p><p class="middle-cont-num">关键词：花姿绰约 淡雅怡人</p><p>山茶花又叫茶花，是中国传统的观赏名花，亦是世界名贵花木之一。</p><img src="img/details/det-10/det-11.jpg" alt=""><p>自古以来，文人墨客都很爱山茶花，它花姿绰约，花色鲜艳，每年春节前的花市中，都能看到各个店面大大小小的盆栽山茶花，一颗颗饱满的花苞充满对未知的遐想，含苞待放的样子让人忍不住就想带它回家。</p><img src="img/details/det-10/det-12.jpg" alt="">',false,8544,3551,'img/productList/list-10.jpg',0),
(11,'家具配色之陶瓷粉','img/details/det-11/det-1.jpg','家居搭配','2019-01-20','不同的色调能引起人不同的感官体验，暖色调极具亲和力，冷色调给人清冷的距离感。那么，是否有一种颜色既有暖色调的亲和也有冷色调的高级感呢？答案肯定是有的，陶瓷粉就是其中的代表。','','','<p>最近看了几期家装类节目，普遍年轻人都对性冷淡家居风格有色彩误区，他们认为性冷淡风只有黑灰白三种。其实‘性冷淡风’的家居空间也包含其他颜色，也可以非常出彩。</p><img src="img/details/det-11/det-2.jpg" alt=""><p>温柔的粉色搭配金色或其他亮色的配饰，更显甜蜜清新。</p><img src="img/details/det-11/det-3.jpg" alt=""><p>灰、白色与陶瓷粉能打造出优雅又朴素的氛围，显得静谧而沉稳。</p><img src="img/details/det-11/det-4.jpg" alt=""><p>而明亮的粉色也显得家居环境不会过于沉闷。</p><img src="img/details/det-11/det-5.jpg" alt=""><p>陶瓷粉主打的工业风家居，两种颜色不但不违和，反而显得更加高级。</p><img src="img/details/det-11/det-6.jpg" alt=""><img src="img/details/det-11/det-7.jpg" alt=""><p>PS：在设计家居风格时，颜色的分布与占比要根据实际情况而定。若房间空间较大，需要粉色占比多些，更显屋子热闹；若房子面积较小，可酌情减少粉色的使用，加入少许冷色调，更显高级。</p><img src="img/details/det-11/det-8.jpg" alt="">',false,5125,661,'img/productList/list-11.jpg',0),
(12,'家居配色之迷人复古黄','img/details/det-12/det-1.jpg','家居搭配','2018-11-21','复古风掀起一波又一波时尚浪潮，成为时下热度超高的家装标签。而暖黄色作为复古风格中的经典色系，十分适合在这个季节入手，超多选择的同时还十分百搭，是好看又吸睛的存在。','','','<p>现在暖黄色火到什么程度呢？今年世姐张梓琳在微博上曝光了自己家的内景，少女心的配色，大量的北欧风绿植，每一块区域都有一些复古黄点缀，整体布置十分简洁明快，可以说是摩登家装布置的典范了。</p><img src="img/details/det-12/det-2.jpg" alt=""><img src="img/details/det-12/det-3.jpg" alt=""><p>柠檬黄是黄色系中最明快的色彩，尝试用它来点缀以白色墙体为主色调的家，给人轻快，充满希望和活力的感觉。可以衬托出高级的复古感，而材质较硬的姜黄色家具则更有现代感。</p><img src="img/details/det-12/det-4.jpg" alt=""><p>当蓝色、白色等冷色调作为主色调时，温暖的棕黄色是不二之选，这样适当的色彩介入，让整个空间变得更加和谐、沉稳和静谧，营造出舒适、温馨的气氛，又让空间不至于呆板。</p><img src="img/details/det-12/det-5.jpg" alt=""><img src="img/details/det-12/det-6.jpg" alt=""><p>姜黄是复古黄系列里比较有个性的色彩，没有柠檬黄的刺眼，也没有土黄的暗沉，透着淡淡水洗感。从去年开始，不少品牌不约而同推出姜黄色主题单品，有着复古感十足的效果。沙发靠垫、榻榻米、椅等家居物品可以选用姜黄色做整个家装的点缀。</p><img src="img/details/det-12/det-7.jpg" alt=""><p>粉色和姜黄是经常被放在一起搭配的颜色，在卧室中使用这两个颜色的组合是不错的选择，大白墙包围的寡淡空间，或是深色墙面下的暗调空间，都可以用它俩的组合色调剂一下。不仅会减少单纯的黄色带来的“土气”与厚重，也会中和粉色的不成熟，看起来也十分清爽。</p><img src="img/details/det-12/det-8.jpg" alt=""><p>而同色系的黄色也能放在一起搭配，这样能最大程度的烘托气氛，用天然木材的棕色与针织物上的姜黄色填充空间，让家充满暖意同时气质自然亲切。</p><img src="img/details/det-12/det-9.jpg" alt=""><img src="img/details/det-12/det-10.jpg" alt="">',false,5514,2615,'img/productList/list-12.jpg',0),
(13,'最适合十二星座的主体婚礼','img/details/det-13/det-1.jpg','婚嫁','2018-07-27','不同的星座有着不同的个性、喜好，那么根据十二星座的性格特点，最适合每个星座的婚礼是什么样子的呢？','本期小编特意为大家总结并挑选了适合十二星座的各个婚礼形式，一起来看看属于你的梦幻婚礼吧！','','<p class="middle-cont-num">水瓶座</p><p class="middle-cont-num">1月20日-2月18日</p><p>幸运花：风信子、蕾丝</p><p>水瓶座睿智、独立、有强烈的个人风格，喜欢简约简单的环境。对水瓶座而言，传统的婚礼主题无法满足她们，酒堡、创意园区等地是很棒的选择。</p><p>推荐婚礼：酒堡婚礼、工厂婚礼</p><img src="img/details/det-13/det-2.jpg" alt=""><img src="img/details/det-13/det-3.jpg" alt=""><img src="img/details/det-13/det-4.jpg" alt=""><p class="middle-cont-num">双鱼座</p><p class="middle-cont-num">2月19日-3月20日</p><p>幸运花：紫罗兰、郁金香</p><p>双鱼座都渴望被呵护，被宠爱，对双鱼座而言，婚礼的主题最需要把浪漫表达到极致才好。碧绿的草地、幽静的花园、清新的湖畔、椰风徐徐的沙滩，都是双鱼心中举行婚礼的好地方。拥有浓浓少女心的双鱼，最喜欢飘逸的纱幔，气球，鲜花拱门……</p><p>推荐婚礼：海岛婚礼、花园婚礼</p><img src="img/details/det-13/det-5.jpg" alt=""><img src="img/details/det-13/det-6.jpg" alt=""><p class="middle-cont-num">白羊座</p><p class="middle-cont-num">3月21日-4月20日</p><p>幸运花：雏菊、樱花</p><p>热情活泼，不拘小节的白羊对婚礼充满了各种疯狂的想法。她更希望每一位参加的来宾都感受到自己的热情，参与到婚礼中来，和大家一起庆祝，分享幸福的喜悦。</p><p>推荐婚礼：派对婚礼</p><img src="img/details/det-13/det-7.jpg" alt=""><p class="middle-cont-num">金牛座</p><p class="middle-cont-num">4月21日-5月20日</p><p>幸运花：矮牵牛、马蹄莲</p><p>牛牛是向往美好最极致的星座，她们向往的结婚地点应该是某个岛屿，也会是某个城堡，反正是一个风景如画的地方，而这里必须满足她们对美的所有想象。</p><p>推荐婚礼：城堡婚礼</p><img src="img/details/det-13/det-8.jpg" alt=""><p class="middle-cont-num">双子座</p><p class="middle-cont-num">5月21日-6月21日</p><p>幸运花：羊齿蕨、铃兰</p><p>作为一个典型的精分的星座，双子座贪玩、喜好新鲜事物，对婚礼的唯一要求就是有创意，这也非常符合大双子的性格。公交车婚礼、极地夜空婚礼、迪士尼乐园……婚礼形式不重要，好玩就好。</p><p>推荐婚礼：主题鲜明的有个性的婚礼</p><img src="img/details/det-13/det-9.jpg" alt=""><img src="img/details/det-13/det-10.jpg" alt=""><img src="img/details/det-13/det-11.jpg" alt=""><p class="middle-cont-num">巨蟹座</p><p class="middle-cont-num">6月22日-7月22日</p><p>幸运花：百合花、洋桔梗</p><p>巨蟹恋家顾家的性格直接影响了她们婚礼的主题，所以大部分巨蟹不太会大胆尝试特别的婚礼形式，对她们来说，婚礼温馨就够了。</p><p>推荐婚礼：新中式婚礼、传统酒店婚礼</p><img src="img/details/det-13/det-12.jpg" alt=""><img src="img/details/det-13/det-13.jpg" alt=""><p class="middle-cont-num">狮子座</p><p class="middle-cont-num">7月23日-8月22日</p><p>幸运花：向日葵、虞美人</p><p>狮子座热心、慷慨、有能够handle住一切的力量，率真的狮子更喜欢环境原始粗狂热情，能够和大家一起HIGH翻全场！</p><p>推荐婚礼：沙漠婚礼、工业风主题婚礼</p><img src="img/details/det-13/det-14.jpg" alt=""><img src="img/details/det-13/det-15.jpg" alt=""><p class="middle-cont-num">处女座</p><p class="middle-cont-num">8月23日-9月22日</p><p>幸运花：大波斯菊</p><p>作为被黑的最惨的星座之一，处女座有着对完美极致追求的韧劲和执行力，而简洁清晰便是处女座对婚礼的唯一要求。</p><p>推荐婚礼：森林婚礼、草坪婚礼</p><img src="img/details/det-13/det-16.jpg" alt=""><img src="img/details/det-13/det-17.jpg" alt=""><p class="middle-cont-num">天秤座</p><p class="middle-cont-num">9月23日—10月23日</p><p>幸运花：非洲菊、猫尾花</p><p>天秤座的宝宝对细节的处理很较真，教堂婚礼既满足天秤座对细节的精致追求，又不乏个性与浪漫的表现，一定会让天秤宝宝们终生难忘！</p><p>推荐婚礼：教堂婚礼</p><img src="img/details/det-13/det-18.jpg" alt=""><p class="middle-cont-num">天蝎座</p><p class="middle-cont-num">10月24日-11月22日</p><p>幸运花：剑兰、天堂鸟</p><p>天蝎座神秘儒雅有魅力，蝎子们的婚礼，那必须是“独一无二”的，要达到这样的目的，首选旅行婚礼！</p><p>推荐婚礼：旅行婚礼</p><img src="img/details/det-13/det-19.jpg" alt=""><img src="img/details/det-13/det-20.jpg" alt=""><p class="middle-cont-num">射手座</p><p class="middle-cont-num">11月23日-12月21日</p><p>幸运花：玛格丽特、素心兰</p><p>射手座喜欢表现自己，喜欢自由。派对婚礼可以很好地释放射手座无拘无束的性格，自由好玩最得射手心。</p><p>推荐婚礼：派对婚礼、游泳池婚礼</p><img src="img/details/det-13/det-21.jpg" alt=""><img src="img/details/det-13/det-22.jpg" alt=""><p class="middle-cont-num">摩羯座</p><p class="middle-cont-num">12月22日-1月19日</p><p>幸运花：满天星、绣球</p><p>最有耐心的摩羯，也最内向、传统，婚礼上肯定会有很多传统的元素。私密性好的家庭庭院婚礼是很棒的选择。</p><p>推荐婚礼：家庭庭院婚礼</p><img src="img/details/det-13/det-23.jpg" alt=""><img src="img/details/det-13/det-24.jpg" alt="">',false,55525,11454,'img/productList/list-13.jpg',0),
(14,'省钱的三种婚礼花艺设计方案','img/details/det-14/det-1.jpg','婚嫁','2018-12-07','说到婚礼，最不能缺少的元素就是鲜花了，从新娘手捧到新郎胸花，从宾客桌花到拱门装饰，大到现场鲜花墙和餐桌花，小到仪式区的花瓣雨，虽然各个婚礼花艺设计不同，但鲜花的出现都能给人带来幸福的好运气。','之前婚庆行业有做过大致的调查，一场婚礼中花材的费用会占到总费用的三分之一。不得不说，在整个婚礼花费中鲜花算得上是隐形的高消费。那在婚礼花艺设计这块，如何做到既省钱又精致呢，需要从下面这三方面考虑。','','	<p class="middle-cont-num">1.</p><p class="middle-cont-num">控制花材成本</p><p>花材产量、产地、是否应季、运输成本等，这些会直接影响花材的成本，所以在婚礼花艺策划时控制好花材的成本很重要。</p><p></p>	<p>以国内婚礼为例，花材分进口和国产两种，进口花花型硕大、色泽艳丽，但成本太高，所以与其大费周折买进口，倒不如考虑国内的品种，这很适合预算有限的婚礼。在花材选择上可以直接使用蝴蝶兰、玫瑰、绣球、多肉或藤蔓类叶材等。</p><p></p><p>花材的选择标准：预算范围内，选择体量大、耐脱水的花材。这类型花材能尽最大可能提升花艺作品的量感，得到惊人的视觉效果。</p><img src="img/details/det-14/det-2.jpg" alt=""><img src="img/details/det-14/det-3.jpg" alt=""><img src="img/details/det-14/det-4.jpg" alt=""><p class="middle-cont-num">2.</p><p class="middle-cont-num">不要局限于鲜花</p><p>在婚礼花艺设计中不要局限于鲜花，绢花、纸花等现在也成了流行的“花”趋势。低成本耗材，既能重复利用，也无需担心它们是否会脱水，影响正常婚礼效果。</p><img src="img/details/det-14/det-5.jpg" alt=""><img src="img/details/det-14/det-6.jpg" alt=""><p class="middle-cont-num">3.</p><p class="middle-cont-num">调整婚礼花艺比重</p><p>婚礼的设计精髓肯定是整体的空间感，如果要减少预算，最好的做法是调整花艺的设计比重，以最小的设计做出超比例的空间氛围。</p>	<p></p>	<p>例如，迎宾区、餐桌、仪式区，这三处是最容易引起宾客注意的区域，所以在设计之初，就应该重点设计这三个区域，当然也能通过气球、折扇等辅助鲜花做出超量感的设计。</p><img src="img/details/det-14/det-7.jpg" alt=""><img src="img/details/det-14/det-8.jpg" alt=""><img src="img/details/det-14/det-9.jpg" alt="">',false,25884,5948,'img/productList/list-14.jpg',0),
(15,'当蛋糕遇上鲜花，当甜蜜邂逅芬芳','img/details/det-15/det-1.jpg','婚嫁','2018-05-05','几乎所有女生对甜品和鲜花都是丝毫没有抵抗力的，今天呢，我们就来看看当蛋糕遇上鲜花，当甜蜜遇上芬芳是何种的治愈和幸福。','','','<p>鲜花蛋糕可以说是融合了甜蜜、芬芳等一切浪漫美好的代名词，种种美好交织在一起，再加上超治愈系的色彩和别致的外观，分分秒秒击中少女心。</p><img src="img/details/det-15/det-2.jpg" alt=""><p>说到鲜花蛋糕的运用，目前主要是婚礼甜点台运用较多，日常食用的蛋糕中，鲜花点缀的情况相对较少，因为对于数量繁多的鲜花而言，能够食用的花卉依旧是一小部分，大部分花是不能随便食用的，而市面上售卖的鲜花大都喷过杀虫剂，远未达到食品安全要求；另外，有些花材也可能会跟奶油、糖等物质发生化学反应；还有就是，鲜花蛋糕运输的过程中花粉的洒落也有可能会造成食用花粉以及后续的危害。</p><img src="img/details/det-15/det-3.jpg" alt=""><p>北京某五星酒店厨师长盛某表示，不是所有的鲜花都适合装饰在甜点上，有些花朵含毒素。另外，有些花卉是否会和蛋糕中的奶油、糖等物质发生化学反应也不明确。他们偶尔也会在甜点上用到鲜花，但选材严谨。“我们会采用业内已经使用过一段时间、证明没有问题的花的品种，而且必须是经过QS认证的食品级花材，来自真正的有机农场，种植全程不含农药。”</p><img src="img/details/det-15/det-4.jpg" alt=""><p>所以呢，日常食用蛋糕不建议随便购买鲜花蛋糕，但婚礼中绝对推荐鲜花蛋糕，因为颜值即一切嘛。今天我们就婚礼蛋糕来介绍几种：</p><p class="middle-cont-num">1.</p><p class="middle-cont-num">鲜花&裸蛋糕</p><p>外层只有奶油或糖霜的裸蛋糕，可以直接拿来点缀上鲜花装饰，秒变小清新，特别适合一些森系户外婚礼，花朵的选择上，可以五彩缤纷，可以选择同色系，也可以选择淡雅的颜色，简单几朵就可以营造完全不一样的感觉，有了鲜花的点缀，裸蛋糕不再是裸蛋糕。</p><img src="img/details/det-15/det-5.jpg" alt=""><img src="img/details/det-15/det-6.jpg" alt=""><img src="img/details/det-15/det-7.jpg" alt=""><p class="middle-cont-num">2.</p><p class="middle-cont-num">多肉&蛋糕</p><p>可爱萌萌的迷你多肉搭配清新简单的裸蛋糕，大写的“文艺”二字！喜欢肉肉的新娘不可错过。</p><img src="img/details/det-15/det-8.jpg" alt=""><img src="img/details/det-15/det-9.jpg" alt=""><img src="img/details/det-15/det-10.jpg" alt=""><p class="middle-cont-num">3.</p><p class="middle-cont-num">花环&蛋糕</p>	<p>鲜花配蛋糕已经够浪漫了，花环配蛋糕简直就是浪漫中的浪漫之作。不管是简单的叶材构成的花环还是搭配花朵的花环都给人眼前一亮的感觉。</p><img src="img/details/det-15/det-11.jpg" alt=""><img src="img/details/det-15/det-12.jpg" alt=""><p class="middle-cont-num">4.</p><p class="middle-cont-num">仙人掌&蛋糕</p>	<p>仙人掌，当今文艺的代名词，喜欢仙人掌的也越来越多，这两者的搭配还是比较少见的，可以说是比较别致的搭配，想要追求个性博眼球的新娘可以尝试。</p><img src="img/details/det-15/det-13.jpg" alt=""><p class="middle-cont-num">5.</p><p class="middle-cont-num">叶材&蛋糕</p>	<p>如今，户外婚礼正受到越来越多的年轻人的追捧，而户外婚礼主题中，必不可少的就是清新自然的森系风格婚礼，而森系必不可少的当然是绿色的叶材，所以呢，也就有了叶材和蛋糕的搭配，是不是清新十足。</p><img src="img/details/det-15/det-14.jpg" alt=""><img src="img/details/det-15/det-15.jpg" alt="">',false,15441,6555,'img/productList/list-15.jpg',0),
(16,'满天星婚礼，给足你浪漫和梦幻','img/details/det-16/det-1.jpg','婚嫁','2018-05-16','雪白蓬松的满天星花朵细小而轻盈，浪漫而梦幻，近年来婚礼中也越来越多的见到它的身影，虽花语是“甘愿做配角”，但在婚礼中，满天星的颜值绝对并不只是用来做配角花材，它也可以纵身一跃成为婚礼主花材。','纯白浪漫的满天星可以说是近年来婚礼花卉界中的耀眼新星了，它洁白无瑕，它梦幻浪漫，花朵不大，但也正因众多细小的花朵材凝聚出了一种天然的梦幻感。这个夏天，办场清新浪漫的满天星婚礼如何？','','<p class="middle-cont-num">1、</p><p class="middle-cont-num">婚礼拱门</p><p>如梦如幻的满天星拱门，是不是仿若踏进仙境般的感觉。</p><img src="img/details/det-16/det-2.jpg" alt=""><img src="img/details/det-16/det-3.jpg" alt=""><p class="middle-cont-num">2、</p><p class="middle-cont-num">婚礼餐台布置</p><p>星星点点的满天星，让整个婚礼餐台增添一种俏皮感。</p><img src="img/details/det-16/det-4.jpg" alt=""><img src="img/details/det-16/det-5.jpg" alt=""><img src="img/details/det-16/det-6.jpg" alt=""><img src="img/details/det-16/det-7.jpg" alt=""><p class="middle-cont-num">3、</p><p class="middle-cont-num">座椅装饰</p><p>婚礼当天，如果只简单的摆放来宾座椅未免过度单调，满天星的点缀下是不是增添了更多的灵动感，同时呢，推荐满天星跟白色纱幔一起搭配，微风吹起，灵动十足。</p><img src="img/details/det-16/det-8.jpg" alt=""><img src="img/details/det-16/det-9.jpg" alt=""><p class="middle-cont-num">4、</p><p class="middle-cont-num">婚礼蛋糕</p><p>婚礼必备的甜点台自然也不能少了满天星的点缀，瞧，这样的蛋糕看一眼就走不动了。</p><img src="img/details/det-16/det-10.jpg" alt=""><img src="img/details/det-16/det-11.jpg" alt=""><p class="middle-cont-num">5、</p><p class="middle-cont-num">手捧花</p><p>满天星作为众花材中不起眼的一个，很多时候都是被忽略的，但对于喜欢小清新文的手捧花的新人们，推荐可以直接用满天星来做一束满天星手捧花，小小的一束，簇拥在一起的小花给人一种满满都是爱的感觉。</p><img src="img/details/det-16/det-12.jpg" alt=""><p>另外：满天星也可以跟一些其它花材搭配在一起，蓬松感之余，多一点点彩色的点缀也是很美的。</p><img src="img/details/det-16/det-13.jpg" alt=""><img src="img/details/det-16/det-14.jpg" alt=""><img src="img/details/det-16/det-15.jpg" alt=""><p class="middle-cont-num">6、</p><p class="middle-cont-num">新娘头饰</p><p>花环给人一种非常仙儿的感觉，对于长发的新娘来说，不需要太负责的发饰，长发披肩配个花环，你就是婚礼当天最美的仙女。</p><img src="img/details/det-16/det-16.jpg" alt=""><img src="img/details/det-16/det-17.jpg" alt=""><img src="img/details/det-16/det-18.jpg" alt=""><p class="middle-cont-num">7、</p><p class="middle-cont-num">花童</p><p>给小花童们也准备一款可爱的花环吧，它会让你的婚礼更加俏皮可爱。</p><img src="img/details/det-16/det-19.jpg" alt=""><img src="img/details/det-16/det-20.jpg" alt=""><p class="middle-cont-num">8、</p><p class="middle-cont-num">新郎胸花</p><p>配合婚礼主题，新郎的胸花可以直接用满天星单种花材，也或者选一些其它较迷你的花材来搭配。</p><img src="img/details/det-16/det-21.jpg" alt=""><img src="img/details/det-16/det-22.jpg" alt="">',false,65444,25541,'img/productList/list-16.jpg',0),
(17,'秋季养好肉你不得不知道的几件事','img/details/det-17/det-1.jpg','植物百科','2017-09-28','抓紧这个秋！可能是让你的多肉变美的最后时机。熬过了炎热夏季，肉肉终于迎来了凉爽的秋天。温度降低、温差变大、紫外线充足，秋天无疑是多肉变美的最适宜季节。','今天给大家简单系统讲解一下秋天养好多肉应该从哪几方面着手。','','<p class="middle-cont-num">1、</p><p class="middle-cont-num">喂饱水是把多肉“养肥”的小秘密</p><p>多肉是营养器官肥大的植物，单从视觉上看便能知道多肉是需水植物，干透浇透是多肉浇水的必须原则，肉肉肥美就看水分。室内养护多肉的切记勿浇太多水，而家里有露养条件的，完全可以大胆浇水。什么是露养条件呢？直白一点儿就是给肉肉营造成自然野生的环境状态。</p><img src="img/details/det-17/det-2.jpg" alt=""><img src="img/details/det-17/det-3.jpg" alt=""><p class="middle-cont-num">2、</p><p class="middle-cont-num">秋季昼夜温差大，给多肉增色提供了绝佳机会。秋季多通风，加速肉肉增色。“控水+光强+温度”是多肉增色三要素，肯定很多人好奇怎样通过水、温度和光照改变肉的颜色。在此科普一下，改变肉的颜色根本做法是改变肉内部色素比例。浇水过多，肉会拼命生长，叶绿素比例增多，非良性徒长，除不能增色外，更严重可能导致肉肉颜色变浅直至死掉；控水之后，肉内部生长色（绿色）减少，其它颜色变深；温差大时，肉为了御寒叶片变暗，会形成新的花色素，最后颜色变深。</p><img src="img/details/det-17/det-4.jpg" alt=""><img src="img/details/det-17/det-5.jpg" alt=""><p class="middle-cont-num">3、</p><p class="middle-cont-num">喜欢紫外线的多肉</p><p>秋天紫外线充足，为多肉增色提供了便利。但值得注意的是，切记突然暴晒、突然“乘凉”，总要有个循序渐进的过程嘛，即刚入秋时，先把多肉放在散射光位置，适应紫外线环境后放在阳光下直射，有条件的可用遮阳网遮光。如果有必要，可购置多肉植物补光灯。</p><img src="img/details/det-17/det-6.jpg" alt=""><p class="middle-cont-num">4、</p><p class="middle-cont-num">土壤是安身立命的关键</p><p>肉肉对土壤要求极高，需透气性极好的土壤条件。田园土最好不要用，用的话也必须加入透气性好的颗粒土。给大家推荐几款常用的多肉土壤，绿沸石、鹿沼土、硅藻土、日向石、火山岩、赤玉土这几款土资深肉党肯定知道。</p><p class="middle-cont-num">绿沸石</p><img src="img/details/det-17/det-7.jpg" alt=""><p class="middle-cont-num">鹿沼土</p><img src="img/details/det-17/det-8.jpg" alt=""><p class="middle-cont-num">硅藻土</p><img src="img/details/det-17/det-9.jpg" alt=""><p class="middle-cont-num">日向石</p><img src="img/details/det-17/det-10.jpg" alt=""><p class="middle-cont-num">火山岩</p><img src="img/details/det-17/det-11.jpg" alt=""><p class="middle-cont-num">赤玉土</p><img src="img/details/det-17/det-12.jpg" alt=""><p>肉肉品种丰富，形态万千，不同科属的肉肉养护条件也略不同。</p><p></p><p>但基本抓住这几点，过了这个秋天，你的肉可能成为传说中“别人家的肉”啦！</p><img src="img/details/det-17/det-13.jpg" alt=""><img src="img/details/det-17/det-14.jpg" alt=""><img src="img/details/det-17/det-15.jpg" alt="">',false,4144,3214,'img/productList/list-17.jpg',0),
(18,'除了鲜花，多肉也可以表明爱意','img/details/det-18/det-1.jpg','植物百科','2018-03-14','在平日里，大多数人都会选择用送鲜花来表明对另一半的爱意。其实有一种多肉植物，它也可以更加直接的为你表明爱意！','','','	<p>在平日里，大多数人都会选择用送鲜花来表明对另一半的爱意。其实有一种多肉植物，它也可以更加直接的为你表明爱意！它就是叶子形状很像小心心的吊灯花属多肉植物“爱之蔓”。</p><img src="img/details/det-18/det-2.jpg" alt=""><p>之蔓,是夹竹桃科吊灯花属等多年生植物，多肉植物。又名心蔓、吊金钱、蜡花、一寸心等。植株呈垂吊的藤蔓状，叶稍肉质、呈心形，有块茎。爱之蔓仅在中国市面上较为常</p><img src="img/details/det-18/det-3.jpg" alt=""><p>爱之蔓具蔓性，可匍匐于地面或悬垂，长度可达150-200cm。叶心形，对生，长1.0-1.5cm，宽约1.5cm，叶面上有灰色网状花纹，叶背呈紫红色；叶腋处会长出圆形块茎，称“零余子”，有贮存养分、水分及繁殖的功用。春、夏季时，成株会开出红褐色、壶状的花，长约2.5cm，花后结出羊角状的果实。</p><img src="img/details/det-18/det-4.jpg" alt=""><p>爱之蔓适宜的生长温度为15~25℃，但也能耐受夏季35℃的高温，能耐受冬天10℃左右的低温，对温度的适应性较强。性喜散射光，忌强光直射。不喜肥，特别忌施高磷钾肥。由于其根部有硕大的块茎，能够储存养分和水分，是一种非常耐旱的植物。</p><img src="img/details/det-18/det-5.jpg" alt=""><p>肉肉品种丰富，形态万千，不同科属的肉肉养护条件也略不同。</p><p></p><p>1、温度控制</p><p>要想让爱之蔓快速生长，就要将其放置在最适宜的环境，控制好温度是首要的。温度在15-20℃时生长较快，并且株形颜色比较美观！当温度过高或过低，生长都会受到抑制，还容易滋生病虫害。</p><p></p><p>2、水份控制</p><p>都说要让多肉快速的生长，就要适当的控水！没错，爱之蔓有块茎，可以储存一定的水分，所以日常浇水要观察干湿程度，不要频繁浇水！避免烂根，影响它的正常生长！</p><p></p><p>3、避免强光直射</p><p>爱之蔓喜温，但夏季要避免强光直射，否则叶片容易灼伤，影响生长。</p><p></p><p>4、爆盆秘籍</p><p>给大家说一个小技巧，要想爱之蔓多多爆盆，不仅只靠养护，扦插也是很关键的！只要摘下长有1-3个叶片的茎，稍微晾干，然后插入土中，加水，套上塑料膜保湿，等待生根就可以！</p><img src="img/details/det-18/det-6.jpg" alt=""><p>爱之蔓的花语：心心相印</p><img src="img/details/det-18/det-7.jpg" alt=""><p>爱之蔓天生的心形的叶子，并且还是对生，两两成对，给人一种心心相印的感觉。很有恋爱的感觉，就像是爱情宣言一般。它似乎在传递着爱情，很多人把它的心形叶子当成是爱情的标记。</p><img src="img/details/det-18/det-8.jpg" alt="">',false,25411,6521,'img/productList/list-18.jpg',0),
(19,'铜钱草|我不圆但我可爱呀','img/details/det-19/det-1.jpg','植物百科','2018-07-23','铜钱草，叶子既像是迷你版的荷叶，又有点像古代的铜钱，非常可爱，其名字也有着团团圆圆、好运连连的寓意，而且名字沾上了“铜”、“钱”二字，可以说是一种非常吉祥的植物。','','','<p>说到铜钱草，首先很多人会发出“咦咦，这不是镜面草吗”的疑问，的确是这样，不认真对比还真看不出来呢，那么它俩的最大区别是什么呢？其实就是叶片形状，铜钱草的叶片一圈带点小“刺儿”，而镜面草则是比较圆滑的弧，另外铜钱草的价格则是远远低于镜面草的。</p><img src="img/details/det-19/det-2.jpg" alt=""><p>这个叶片圆溜溜的则是镜面草，是不是也很Q。</p><img src="img/details/det-19/det-3.jpg" alt=""><p>小小一株铜钱草摆在家里装点家居可谓可爱极了，那怎么才能养好铜钱草呢？</p><img src="img/details/det-19/det-4.jpg" alt=""><p class="middle-cont-num">1</p><p class="middle-cont-num">温度</p><p>铜钱草喜温暖潮湿的环境，但不耐寒，在10～25℃的温度环境中生长良好，夏季温度升至32℃以上时，会停止生长，冬季温度不得低于5℃。</p><img src="img/details/det-19/det-5.jpg" alt=""><p class="middle-cont-num">2</p><p class="middle-cont-num">光照</p><p>铜钱草耐阴，以半日照或遮阴处为佳，忌强烈阳光直射，环境越荫蔽越容易导致植株叶片腐烂。铜钱草不争光，但若每天让铜钱草接受4～6小时的散射光照，会让铜钱草长势更好。</p><img src="img/details/det-19/det-6.jpg" alt=""><p class="middle-cont-num">3</p><p class="middle-cont-num">病虫害</p><p>铜钱草不易生虫，生长适应能力强，无须刻意护理，只要时常向叶面喷水清洗叶片，并注意植物生长密集后的通风即可，否则容易出现叶片黄化。</p><img src="img/details/det-19/det-7.jpg" alt=""><p class="middle-cont-num">4</p><p class="middle-cont-num">修剪</p><p>铜钱草叶子过密时需及时摘除枯萎的底叶和外层老叶、病叶，以改善光照、通风条件。春季当盆内长满根系时换盆，并分株整形。</p><img src="img/details/det-19/det-8.jpg" alt=""><p class="middle-cont-num">5</p><p class="middle-cont-num">土壤</p><p>铜钱草其抗旱性不强，最适宜在细致、偏酸、潮湿、肥力低的壤土中生长。</p><img src="img/details/det-19/det-9.jpg" alt=""><p class="middle-cont-num">6</p><p class="middle-cont-num">肥料</p><p>铜钱草对肥料的需求量较多，生长旺盛阶段铜钱草每隔2～3周追肥一次，如种于盆栽或容器中，则需少量施肥，如速效肥花宝二号，能在水中维持较长时间的肥效，保证或水质呈微酸性或中性即可。</p><img src="img/details/det-19/det-10.jpg" alt=""><p class="middle-cont-num">7</p><p class="middle-cont-num">水分</p><p>铜钱草喜欢比较湿润的生长环境，并且由于其叶片多，蒸腾量大，夏季要经常向植株喷水，以保持较高的空气湿度；冬季盆土以偏干为宜，浇水遵循宁湿毋干的原则，忌积水，否则容易烂根。北方的自来水要日晒静置1～2天使用，生长期每2～3大浇水1次，以保持盆土湿润。</p><img src="img/details/det-19/det-11.jpg" alt=""><p>很多人喜欢水培铜钱草，如果选择水培：取一段带2个节以上的根，放到清水里泡上2天，铜钱草节的地方就会长出一些小根须，有时候还会长出小叶子，水培的话，一定要每周换一次水并加入观叶植物专用营养液。如果使用土培，则一定要长期保水，每日清晨要浇一次水，水分不宜过足，以底孔有水流出为度，保证盆土湿透即可。</p><img src="img/details/det-19/det-12.jpg" alt="">',false,6154,3251,'img/productList/list-19.jpg',0),
(20,'是花还是菜呢？','img/details/det-20/det-1.jpg','植物百科','2018-11-29','既可以是冬季餐桌上的美味佳蔬，又是组装冬季绚丽花坛图案的花卉，可谓真正的赏食兼备，它就是羽衣甘蓝。','','','<p>北京冬季的街道上总少不了今天要介绍的这个主角的身影，它就是羽衣甘蓝。羽衣甘蓝原产西欧，如今在英国、荷兰、德国、美国等国种植较多。近年来，随着观光农业经济的发展和对城市绿化、美化的要求，羽衣甘蓝的引种栽培受到大量关注，目前，我国已培育出了数个不同类型杂交一代新品种，这些品种在北京、河北、山东、江苏、四川等多个试点的种植均表明，羽衣甘蓝在晚秋至早春的景观效果明显，抗冻性、观赏性强，品质优良。</p><img src="img/details/det-20/det-2.jpg" alt=""><img src="img/details/det-20/det-3.jpg" alt=""><p>它是结球甘蓝（卷心菜）的园艺变种，结构和形状与卷心菜非常相似，区别在于羽衣甘蓝的中心不会卷成团。</p><img src="img/details/det-20/det-4.jpg" alt=""><p>羽衣甘蓝叶色丰富而多变，叶形也是不尽相同的，叶的边缘有紫红、绿、红、粉等不同的颜色，叶面则有淡黄、绿等颜色，整个植株形状好像牡丹，所以羽衣甘蓝也被人们地称为"叶牡丹"。</p><img src="img/details/det-20/det-5.jpg" alt=""><img src="img/details/det-20/det-6.jpg" alt=""><img src="img/details/det-20/det-7.jpg" alt=""><img src="img/details/det-20/det-8jpg" alt=""><p>它还有着非常棒的花语：华美 、祝福、吉祥如意。</p><img src="img/details/det-20/det-9.jpg" alt=""><p>羽衣甘蓝观赏期长，叶色极为鲜艳，在公园、街头、花坛会经常见到它的身影，用作绿化镶边、组成各种美丽的图案等。</p><img src="img/details/det-20/det-10.jpg" alt=""><img src="img/details/det-20/det-11.jpg" alt=""><p>在我国华南、西南等城市常用它来美化冬季花坛，耐寒极强、株形整齐，用它装点冬季城市最合适不过了</p><img src="img/details/det-20/det-12.jpg" alt=""><img src="img/details/det-20/det-13.jpg" alt=""><p>羽衣甘蓝的园艺品种形态多样，按高度可分高型和矮型；按叶的形态分皱叶、不皱叶及深裂叶品种；按颜色，边缘叶有翠绿色、深绿色、灰绿色、黄绿色，中心叶则有纯白、淡黄、肉色、玫瑰红、紫红等品种。</p><img src="img/details/det-20/det-14.jpg" alt=""><img src="img/details/det-20/det-15.jpg" alt=""><p>羽衣甘蓝在美国非常受欢迎，因为含钙率是10%,维生素A133%，维生素C134%，并且它能抗氧化、抗癌，还能能让你瘦瘦瘦，100g的羽衣甘蓝热量不到50大卡，营养素含量要比香蕉高几十至几百倍，并且富含蛋白质和膳食纤维，容易让人轻松产生饱腹感，可以说是广大减肥爱好者的福音。</p><img src="img/details/det-20/det-16.jpg" alt=""><p>从理论上来说，羽衣甘蓝和西兰花、包心菜是同属同种的植物，在基因层面上是一样的，因此都是可食用的，但目前它们大多数时候只是在盘子上用来做点缀，或者直接作为观赏植物栽培。</p><img src="img/details/det-20/det-17.jpg" alt=""><p>羽衣甘蓝在花艺应用方面，也绝对会令你眼前一亮，稍加动动心思便能打造出独特的东方花艺作品。</p><img src="img/details/det-20/det-18.jpg" alt=""><p>羽衣甘蓝耐寒性极强，如果你喜欢也可以把它作为你的冬季植物选择之一。</p><img src="img/details/det-20/det-19.jpg" alt=""><img src="img/details/det-20/det-20.jpg" alt=""><img src="img/details/det-20/det-21.jpg" alt=""><img src="img/details/det-20/det-22.jpg" alt="">',false,3198,2251,'img/productList/list-20.jpg',0),
(21,'晶莹剔透的3D果冻蛋糕','img/details/det-21/det-1.jpg','诗意生活','2018-12-19','果冻蛋糕里不仅有花朵，还有锦鲤！','','','<p>色彩和细节极为丰富的花朵在透明的果冻蛋糕里，呈现出晶莹剔透的视觉效果。</p><img src="img/details/det-21/det-2.jpg" alt=""><img src="img/details/det-21/det-3.jpg" alt=""><img src="img/details/det-21/det-4.jpg" alt=""><p>制作果冻蛋糕的原理其实并不复杂，就是将天然色素调色、还未凝固的果冻液体注入已经凝固的透明果冻中，各种形状的模具可以制造出不同形状的花瓣，按照花瓣的排列方式注入色彩即可形成花朵的造型。</p><img src="img/details/det-21/det-5.jpg" alt=""><p>虽说制作原理并不复杂，但Siew的果冻蛋糕复杂而精确，形象地模仿了各种花朵的形态。她的果冻蛋糕看上去像是一片清澈见底的池塘，让人一眼便看到池塘里五彩缤纷的落花。</p><img src="img/details/det-21/det-6.jpg" alt=""><img src="img/details/det-21/det-7.jpg" alt=""><p>活泼生动的小锦鲤让这片“池水”活了起来。</p><img src="img/details/det-21/det-8.jpg" alt=""><p>Siew在两年多以前开始创作3D果冻蛋糕，2017年底，她被被诊断出患有三期乳腺癌，不得不暂停了果冻蛋糕事业，但Siew没有让癌症阻碍自己去做喜欢的事情，在身体状况好转的时候，Siew还在为身边的医生和朋友制作果冻蛋糕。</p><img src="img/details/det-21/det-9.jpg" alt=""><img src="img/details/det-21/det-10.jpg" alt=""><p>在与病魔斗争的过程中，Siew通过制作果冻蛋糕转移了注意力，让自己不在沉溺于病痛和精神上的消极状态。她希望通过自己的技艺，为他人带来一份美好的感受。</p><img src="img/details/det-21/det-11.jpg" alt="">',false,56464,32251,'img/productList/list-21.jpg',0),
(22,'淋面蛋糕，婚礼上的新晋网红','img/video/vdo-4.mp4','诗意生活','2018-05-21','婚礼上，除了新郎和新娘的幸福模样外，宾客更多的关注点应该是美味的婚礼蛋糕。2016年裸蛋糕开始横扫婚礼市场，之后，各种非传统的蛋糕成为婚礼主流。本期要介绍的便是近期开始流行的滴落蛋糕，也叫淋面蛋糕。','','','<p>太宰治在《人间失格》里写过，“女人若是突然哭泣，只要给她一点甜食，她吃后就会恢复平静。”甜点是个好东西，给我们饱腹感的同时，还能感到愉悦。在婚礼中，婚礼蛋糕已然演变成烘托婚礼幸福感的最佳道具。</p><img src="img/details/det-22/det-2.jpg" alt=""><p>本期介绍一款最近流行的Drip cake，又叫淋面蛋糕，是婚礼蛋糕届的新宠。</p><p></p><p>具体做法可参照网上的教程，在裸蛋糕的基础上进行再创作。上方的视频主要是淋面的具体过程感兴趣的话可以直接观看视频。</p><img src="img/details/det-22/det-3.jpg" alt=""><p>而且，滴落蛋糕的装饰步骤也非常简单，只要记住两点就可以了</p><p>第一，蛋糕最好堆得高高的，因为那样滴落的效果才明显。</p><p>第二，蛋糕顶部的装饰颜色越鲜艳，色彩越丰富越好。</p><img src="img/details/det-22/det-4.jpg" alt=""><p>把流行色彩元素运用到蛋糕造型中，淋落的马卡龙酱具有一种磨砂质感，与被渲染的蛋糕胚相映衬，再加上一些香槟玫瑰，使整个蛋糕熠熠生辉。</p><img src="img/details/det-22/det-5.jpg" alt=""><img src="img/details/det-22/det-6.jpg" alt=""><p>淋矮层蛋糕时，若想要效果非常明显，可以将酱汁淋的细些，高低错落无序组合带来几何美感。</p><img src="img/details/det-22/det-7.jpg" alt=""><p>蛋糕胚上除了鲜花外，也可以插着的彩虹棒棒糖、多边形创意巧克力与滴落的橙色果酱遥相呼应，绝对是炫彩又吸睛的一款淋面蛋糕。</p><img src="img/details/det-22/det-8.jpg" alt=""><img src="img/details/det-22/det-9.jpg" alt="">	<p>婚礼蛋糕经常会用鲜花装饰，需要注意的是，用到的鲜花需要是无毒的哦。</p><img src="img/details/det-22/det-10.jpg" alt="">',true,61154,21554,'img/productList/list-22.jpg',1),
(23,'手账中的花花世界','img/details/det-23/det-1.jpg','诗意生活','2018-06-25','她手中的手帐让人如沐春风，每片叶子、每朵花瓣仿佛都在跟你招手，诉说着无尽的美好。','','','<p>Yirang Kim是来自韩国的水彩插画家。 她通过其独特的创作方式去展现自己的水彩插画，从而去表现每一棵植物、每一片树叶、每一朵花朵……</p><img src="img/details/det-23/det-2.jpg" alt=""><img src="img/details/det-23/det-3.jpg" alt=""><p>第一眼看她的作品，满满的小清新和治愈系，简直少女心爆棚。</p><img src="img/details/det-23/det-4.jpg" alt=""><p>她平时最常画一些小植物手绘和一些小家居、食物，可以看的出是认真体验生活和热爱生活的。而她作品的最常呈现方式那就是手帐中的花。</p><img src="img/details/det-23/det-5.jpg" alt=""><p>Yirang Kim她巧夺新意，将花朵和画放在一个画面中，通常她会把她想话的花草画在手帐中，同时呢一旁也会如实还原花草当时的姿态，在散播两份美丽同时仿佛画出了春天的仪式感。</p><img src="img/details/det-23/det-6.jpg" alt=""><p>虽然只是一组简简单单的花卉作品，但她会努力去观察植物最本真的色彩与形态，然后用自己的创作方式去表现出来。水彩的晕染和细致的刻画完完全全把花卉的娇艳和含苞待放体现了出来，感觉每一本手帐都可以做植物画册了。</p><img src="img/details/det-23/det-7.jpg" alt=""><p>每一次摆拍，每一次画的花和真花放在一起，画面色彩异常的统一和谐，有一种特别的形式感。</p><img src="img/details/det-23/det-8.jpg" alt=""><p>乍一看真花和画，是不是有时候甚至一眼分不清哪个是花、哪个是画呢？</p><img src="img/details/det-23/det-9.jpg" alt="">	<p>除了手账，Yirang Kim也会尝试利用一些不同材质的纸张来创作。是不是也别有一番意境。</p><img src="img/details/det-23/det-10.jpg" alt=""><p>除了画植物花卉，她还经常画一些卡通动物、人物简笔画、食物、建筑、肖像等，似乎没有她画不了的。</p><img src="img/details/det-23/det-11.jpg" alt=""><img src="img/details/det-23/det-12.jpg" alt=""><img src="img/details/det-23/det-13.jpg" alt=""><img src="img/details/det-23/det-14.jpg" alt=""><img src="img/details/det-23/det-15.jpg" alt=""><p>将手绘花草挂在墙上，是不是瞬时充满了春天温柔的气息。</p><img src="img/details/det-23/det-16.jpg" alt=""><img src="img/details/det-23/det-17.jpg" alt=""><img src="img/details/det-23/det-18.jpg" alt="">',false,31541,6484,'img/productList/list-23.jpg',0),
(24,'我的100颗多肉记－吉娃娃','img/details/det-24/det-1.jpg','诗意生活','2017-08-15','养了多肉才知道叫吉娃娃的不只有汪星人了！','','','<img src="img/details/det-24/det-2.jpg" alt=""><p class="middle-cont-num">植物名称：吉娃娃</p><p class="middle-cont-num">拉丁名：Echeveria chihuahuaensis</p><p class="middle-cont-num">科属：景天科拟石莲属</p><p class="middle-cont-num">生长季：春秋型</p><p>简介：吉娃娃，因植株美丽，叶尖红晕颇有贵妃醉酒风韵，也有称呼为杨贵妃。株型小巧，叶片呈非常标准的莲座状紧密排布，叶片卵形略包拢，顶端带小尖，小尖常红，叶片绿到蓝绿，略带白粉，秋冬低温季节，充分光照下，叶缘带红晕。吉娃娃喜欢干燥和阳光充足的环境，耐旱，不耐水湿，无明显休眠期。</p><img src="img/details/det-24/det-3.jpg" alt=""><p class="middle-cont-num">作品名称 /彩铅手绘多肉植物－吉娃娃</p><p class="middle-cont-num">绘画形式 / 彩铅</p><p class="middle-cont-num">难易程度 / ★★★</p><p class="middle-cont-num">用   时 / 3个小时</p><p class="middle-cont-num">绘画工具 / </p><p class="middle-cont-num">马可油性36色彩色铅笔</p><p class="middle-cont-num">飞乐鸟32开油性彩铅纸</p><h2>- 步骤 -</h2><p class="middle-cont-num">1.</p><p>怎么可以画准复杂的多肉叶片呢？首先画出整颗多肉的大体轮廓，然后从视线最近的一个叶片画起，再画中间一团叶片，最后完成整颗多肉的线稿。</p><img src="img/details/det-24/det-4.jpg" alt=""><img src="img/details/det-24/det-5.jpg" alt=""><img src="img/details/det-24/det-6.jpg" alt=""><p class="middle-cont-num">2.</p><p>选择一个接近多肉本色的草绿色，从根部画起，画多肉的素描关系。</p><img src="img/details/det-24/det-7.jpg" alt=""><p class="middle-cont-num">3.</p><p>铺完第一遍颜色，注意排线尽量整齐，轻重一致，需要耐心一点，一遍一遍的加深。 </p><img src="img/details/det-24/det-8.jpg" alt=""><p class="middle-cont-num">4.</p><p>用大红色画叶片的红尖尖，需要画出红尖尖的明暗两面，排线的时候可以随着叶片的结构方向来排线。 </p><img src="img/details/det-24/det-9.jpg" alt=""><p class="middle-cont-num">5.</p><p>画完整颗多肉叶片的红尖尖，靠下的叶片外侧偏红，可以浅浅的铺一点红色。</p><img src="img/details/det-24/det-10.jpg" alt=""><p class="middle-cont-num">6.</p><p>增加环境色，用浅绿色画部分叶片，浅绿色普遍画在靠下叶片的外侧。</p><img src="img/details/det-24/det-11.jpg" alt=""><p class="middle-cont-num">7.</p><p>增加暗部环境色，用蓝绿色彩铅给部分叶片内侧上色，尽量让蓝绿色和之前的草绿色融合得自然一点。</p><img src="img/details/det-24/det-12.jpg" alt=""><p class="middle-cont-num">8.</p><p>加深阴影，用钴蓝色加深叶片根部阴影，使叶片更立体更有层次感。</p><img src="img/details/det-24/det-13.jpg" alt=""><p class="middle-cont-num">9.</p><p>用中黄画叶片的边缘及叶片的受光面，整体调整后作品完成。</p><img src="img/details/det-24/det-14.jpg" alt=""><p class="middle-cont-num">10.</p><p>长按图片保存，下载吉娃娃壁纸。</p><img src="img/details/det-24/det-15.jpg" alt=""><p class="middle-cont-num">11.</p><p>吉娃娃的明信片展示</p><img src="img/details/det-24/det-16.jpg" alt="">',false,6514,3544,'img/productList/list-24.jpg',0);

